import { Directive, HostBinding, HostListener, Input } from '@angular/core';
import { ContextMenuComponent } from '../../components/context-menu/context-menu.component';
import { ContextMenuEventService } from '../../services/context-menu-event/context-menu-event.service';

@Directive({
  selector: '[contextMenu]',
})
export class ContextMenuDirective<T> {
  /**
   * The item related to the context menu
   */
  @Input()
  public contextMenuItem?: T;

  /**
   * The component holding the menu item directive templates
   */
  @Input()
  public contextMenu?: ContextMenuComponent<T>;

  /**
   * The directive must have a tabindex for being accessible
   */
  @Input()
  @HostBinding('attr.tabindex')
  public tabindex = 0;

  /**
   * Optionally add a class to make it stylable
   */
  @Input()
  @HostBinding('class.ngx-context-menu-focusable')
  public contextMenuFocusableCSSClass = true;

  /**
   * Accessibility
   *
   * @internal
   */
  @HostBinding('attr.aria-haspopup')
  public ariaHasPopup = 'true';

  constructor(private contextMenuEventService: ContextMenuEventService<T>) {}

  /**
   * @internal
   */
  @HostListener('contextmenu', ['$event'])
  public onContextMenu(event: MouseEvent): void {
    if (this.contextMenu && !this.contextMenu.disabled) {
      this.contextMenuEventService.show({
        anchoredTo: 'position',
        contextMenu: this.contextMenu,
        x: event.clientX,
        y: event.clientY,
        item: this.contextMenuItem,
      });
      event.preventDefault();
      event.stopPropagation();
    }
  }
}

import { Highlightable } from '@angular/cdk/a11y';
import {
  Directive,
  EventEmitter,
  Input,
  Optional,
  Output,
  TemplateRef,
} from '@angular/core';
import { ContextMenuComponent } from '../../components/context-menu/context-menu.component';
import { evaluateIfFunction } from '../../helper/evaluate';

@Directive({
  selector: '[contextMenuItem]',
})
export class ContextMenuItemDirective<T> implements Highlightable {
  /**
   * Optional subMenu component ref
   */
  @Input()
  public subMenu?: ContextMenuComponent<T>;

  /**
   * Is this menu item a divider
   */
  @Input()
  public divider = false;

  /**
   * Is this menu item disabled
   */
  @Input()
  public set disabled(value: boolean | ((item?: T) => boolean)) {
    this.#disabled = value;
  }

  /**
   * @internal
   */
  public get disabled(): boolean {
    return (
      this.passive ||
      this.divider ||
      evaluateIfFunction(this.#disabled, this.item)
    );
  }

  /**
   * Is this menu item passive (for title)
   */
  @Input()
  public passive = false;

  /**
   * Is this menu item visible
   */
  @Input()
  public visible: boolean | ((item?: T) => boolean) = true;

  /**
   * Emits
   */
  @Output()
  public execute: EventEmitter<{
    event: MouseEvent | KeyboardEvent;
    item?: T;
  }> = new EventEmitter();

  /**
   * @internal
   */
  public item?: T;

  /**
   * @internal
   */
  public isActive = false;

  #disabled: boolean | ((item?: T) => boolean) = false;

  constructor(
    @Optional()
    public template: TemplateRef<{ item: T }>
  ) {}

  public setActiveStyles(): void {
    this.isActive = true;
  }

  public setInactiveStyles(): void {
    this.isActive = false;
  }

  /**
   * @internal
   */
  public triggerExecute($event: MouseEvent | KeyboardEvent, item?: T): void {
    if (evaluateIfFunction(this.#disabled, item)) {
      return;
    }

    this.execute.emit({ event: $event, item });
  }
}

