// import { ScrollDispatchModule } from '@angular/cdk/scrolling';
import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { RouterModule } from '@angular/router';
import { ContextMenuModule } from '@perfectmemory/ngx-contextmenu';
import { AppComponent } from './app.component';
import { ChildOneComponent } from './components/child1.component';
import { ChildTwoComponent } from './components/child2.component';

@NgModule({
  bootstrap: [AppComponent],
  declarations: [AppComponent, ChildOneComponent, ChildTwoComponent],
  imports: [
    BrowserModule,
    CommonModule,
    ContextMenuModule,
    RouterModule.forRoot([
      {
        path: 'two',
        component: ChildTwoComponent,
      },
      {
        path: '**',
        component: ChildOneComponent,
      },
    ]),
  ],
})
export class AppModule {}

import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';

const routes: Routes = [];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }

import {
  FullscreenOverlayContainer,
  OverlayContainer,
  OverlayModule,
} from '@angular/cdk/overlay';
import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { ContextMenuContentComponent } from './components/context-menu-content/context-menu-content.component';
import { ContextMenuComponent } from './components/context-menu/context-menu.component';
import { ContextMenuItemDirective } from './directives/context-menu-item/context-menu-item.directive';
import { ContextMenuDirective } from './directives/context-menu/context-menu.directive';

@NgModule({
  declarations: [
    ContextMenuDirective,
    ContextMenuComponent,
    ContextMenuContentComponent,
    ContextMenuItemDirective,
  ],
  providers: [
    { provide: OverlayContainer, useClass: FullscreenOverlayContainer },
  ],
  exports: [
    ContextMenuDirective,
    ContextMenuComponent,
    ContextMenuItemDirective,
  ],
  imports: [CommonModule, OverlayModule],
})
export class ContextMenuModule {}

